import axios from "axios";
import { useEffect, useState } from "react";

function Dashboard() {

   const url="http://127.0.0.1:5000/products" ;
   const [products,setProducts]= useState([]);
   //const [product,setProduct]=({productid:"",producttitle:"",price:"",stock:""});

   const FetchRecords=()=>{
    axios.get(url).then((result)=>{setProducts(result.data)})
   }

  useEffect(()=>{FetchRecords()},[]) 
    return ( <div className='table-responsive'>
        <table className="table table-bordered">
            <thead>
                <tr>
                    <th>productid</th>
                    <th>producttitle </th>
                    <th>price</th>
                    <th>stock</th>
                    <th></th>
                    <th></th>
                </tr>
            </thead>
        <tbody>
          {
            products.map((product)=>{
                return(<tr key={product.productid}>
                    <td>{product.productid}</td>
                    <td>{product.producttitle}</td>
                    <td>{product.price}</td>
                    <td>{product.stock}</td>
                    {/* <td><button className="btn btn-warning" onClick={()=>{EditRecord(product.productid)}}>Edit</button></td>
                    <td><button className="btn btn-danger" onClick={()=>{
                        RemoveRecord(product.productid)}}>Delete</button></td> */}
                </tr>)
            })
          }
        </tbody>
        </table>

    </div> );
}

export default Dashboard;
