import axios from "axios";
import { useEffect, useState } from "react";

function Dashboard() {
  const url = "http://127.0.0.1:5000/products";
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({
    productid: "",
    producttitle: "",
    price: "",
    stock: "",
  });

  const FetchRecords = () => {
    axios.get(url).then((result) => {
      setProducts(result.data);
    });
  };
  const OnTextChanged = (args) => {
    var copyofProduct = { ...product };
    copyofProduct[args.target.name] = args.target.value;
    setProduct(copyofProduct);
  };
  const RemoveRecord = (productid) => {
    console.log(productid);

    var deleteUrl = url + "/" + productid;
    axios.delete(deleteUrl).then((result) => {
      if (
        result.data.affectedRows !== undefined &&
        result.data.affectedRows > 0
      ) {
        FetchRecords();
      }
    });
  };
  const EditRecord = (productid) => {
    for (var i = 0; i < products.length; i++) {
      if (products[i].productid === productid) {
        var productToEdit = { ...products[i] };
        setProduct(productToEdit);
        break;
      }
    }
  };

  useEffect(() => {
    FetchRecords();
  }, []);
  return (
    <div className="container">
      <div className="table-responsive">
        <table className="table table-bordered">
          <tbody>
            <tr>
              <td>productid</td>
              <td>
                <input
                  type="number"
                  name="ProductID"
                  value={product.productid}
                  onChange={OnTextChanged}
                />
              </td>
            </tr>
            <td>producttitle</td>
            <td>
              <input
                type="text"
                name="Title"
                value={product.producttitle}
                onChange={OnTextChanged}
              />
            </td>
            <tr>
              <td>price</td>
              <td>
                <input
                  type="number"
                  name="Price"
                  value={product.price}
                  onChange={OnTextChanged}
                />
              </td>
            </tr>
            <tr>
              <td>stock</td>
              <td>
                <input
                  type="number"
                  name="stock"
                  value={product.stock}
                  onChange={OnTextChanged}
                />
              </td>
            </tr>
            <tr></tr>
          </tbody>
        </table>
      </div>
      <div className="table-responsive">
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>productid</th>
              <th>producttitle </th>
              <th>price</th>
              <th>stock</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => {
              return (
                <tr key={product.productid}>
                  <td>{product.productid}</td>
                  <td>{product.producttitle}</td>
                  <td>{product.price}</td>
                  <td>{product.stock}</td>
                  <td>
                    <button
                      className="btn btn-warning"
                      onClick={() => {
                        EditRecord(product.productid);
                      }}
                    >
                      Edit
                    </button>
                  </td>
                  <td>
                    <button
                      className="btn btn-danger"
                      onClick={() => {
                        RemoveRecord(product.productid);
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
